using KidegaApp.Entities;

namespace KidegaApp.Infrastructure.Data
{
    public static class DbSeeding
    {
        public static void SeedDatabase(KidegaDbContext context)
        {
            seedCategoryIfNotExist(context);
            seedAuthorIfNotExist(context);
            seedBookIfNotExist(context);
        }

        private static void seedCategoryIfNotExist(KidegaDbContext context)
        {
            if(!context.Categories.Any())
            {
                var categories = new List<Category>() 
                {
                    new Category(){Name="Dünya Klasikleri"},
                    new Category(){Name="Bilim Kurgu - Fantastik"},
                    new Category(){Name="Türk Edebiyatı"},

                };
                context.Categories.AddRange(categories);
                context.SaveChanges();
            }
        }

        private static void seedBookIfNotExist(KidegaDbContext context)
        {
            if (!context.Books.Any())
            {
                var books = new List<Book>()
                {
                    new Book(){Title="Satranç", Description="Satranç, Zweig’ın novella türünde eriştiği ustalığı gözler önüne seren en son yapıtı; aynı zamanda bir edebi veda mektubudur. İkinci Dünya Savaşı yüzünden ülkesini terk eden yazarın, çok sevdiği Avrupa’nın içler acısı durumunun ve zorunlu göçün yarattığı derin umutsuzluk sonucu yaşamına son vermesinden bir yıl önce, 1941’de yayımlanmıştır. Buenos Aires’e gitmek üzere New York’tan hareket eden büyük bir geminin yolcuları arasında dünya satranç şampiyonu Mirko Czentovic de vardır. Satranç meraklısı bir grup yolcu kendisiyle oynadıkları ilk partiyi kaybeder. Czentovic ikinci partiyi de kazanmak üzereyken, aniden zuhur eden Dr. B. adlı şahsın müdahale ve yönlendirmesiyle oyun berabere biter. Bunun üzerine Dr. B., Czentovic’le tek başına oynamaya ikna edilir. Ancak bir eğlence olarak başlayan satranç oyunu, Nazilerin yarattığı korku ve dehşet ikliminden payına düşeni alan bu adamda bazı travmatik anıları canlandıracaktır.",
                               AuthorId=1, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9786057490476.jpg", UnitPrice=14, CategoryId = 1},
                    new Book(){Title="80 Günde Devri Alem", Description="Hiç seyahat etmiş miydi? Büyük olasılıkla, çünkü dünya haritasına kimse onun kadar hâkim değildi. Paylaşacak detaylı bilgisinin olmadığı hiçbir yer yoktu. Kulüpte, yolunu kaybetmiş ya da şaşırmış gezginlerle ilgili ortaya atılan binlerce görüşü kimi zaman çok az sözcükle, kısaca ve açık bir şekilde düzeltirdi... Her yeri gezmiş biri olmalıydı, en azından zihninde... Bununla birlikte kesin olan bir şey vardı, o da Phileas Fogg’un senelerdir Londra’dan ayrılmadığıydı. Phileas Fogg, kimsenin hakkında bir şey bilmediği zengin ve kibar bir İngiliz beyefendisidir. Son derece düzenli bir hayat sürmesi, titiz ve dakik olmasıyla ünlüdür. Bir gün üyesi olduğu Londra Reform Kulübü’nde gerçekleştirmesi imkânsız gibi görünen bir konuda iddiaya girer: Dünyanın çevresini 80 günde dolaşacaktır. Fogg tek bir aksilikte her şeyini kaybedebileceği bu seyahate yardımcısıyla çıkar. Onlarca farklı ülkede başına geleceklerden ise habersizdir. 1872’de yayımlandığı günden beri her yaştan okurun hayal gücünü zenginleştiren 80 Günde Devri Âlem, on dokuzuncu yüzyıldaki teknolojik gelişmeler sayesinde uzakların yakınlaşmaya başladığı ve böylece modern kültürün de adım adım değiştiği bir dönemin panoramasını çiziyor.",
                               AuthorId=2, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9786052651698.jpg", UnitPrice=30, CategoryId = 1},
                    new Book(){Title="John Carter 8: Mars Kılıçları", Description="Macera dolu serileriyle tüm dünyayı kasıp kavuran Edgar Rice Burroughs, bilimkurgu yazınına yön veren “John Carter” Barsoom dizisiyle karşınızda!\r\n8. kitap “Mars Kılıçları” ile aksiyon dolu hikâye devam ediyor! 20. yüzyılın ilk “çok ama çok satan” bilimkurgu klasiği; sonraki kuşak tüm bilimkurgu yazarlarının anılarında büyük yeri olan, etkisi Star Wars dünyasından Avatar filmine kadar her bilimkurgu yapıtında hissedilen, eşsiz bir gezegenlerarası macera dizisi...Mars Kılıçları’nda John Carter tekrardan baş kahraman olarak karşımıza çıkıyor ve aksiyon dolu bu maceraya yön veriyor. Ur Jan liderliğindeki bir suikastçı loncasına karşı verdiği mücadele ve kılıç düelloları bizi esere bağlarken, iki rakip bilimadamı Fal Sivas ve Gar Nal’ın rekabetinin ortasında kalıyoruz; kendimizi “yapay beyin” üretme çalışmaları içerisinde buluyoruz.\r\n\r\n\r\nJohn Carter yine aksiyon sınırlarını zorlarken, büyük aşkla bağlı olduğu karısı Dejah Thoris’i kurtarmanın yollarını arıyor ve kılıçlar hiç susmuyor! Edgar Rice Burroughs’un Barsoom dünyası, şanına yaraşır aksiyon dolu macerasına bizleri davet ediyor. Hadi, bu sonsuz evrene siz de katılın!",
                               AuthorId=3, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9786256438262.jpg", UnitPrice=87, CategoryId = 2},
                    new Book(){Title="İnce Memed 1", Description="Otuz iki yıllık bir zaman diliminde yazılan İnce Memed dörtlüsü, düzene başkaldıran Memed'in ve insan ilişkileri, doğası ve renkleriyle Çukorava'nın öyküsüdür. Yaşar Kemal'in söyleşiyle \"içinde başlakdırma kurduyla doğmuş\" bir insanın, \"mecbur adam\"ın romanı. Abdi Ağa'nın zulmüyle köyünü terk etmek zorunda kalan Memed, Ağa'nın yeğeniyle evlendirilmek üzere olan Hatçe'yi kaçırır.\r\n\r\nAbdi Ağa'yı yaralayan, yeğenini de öldüren Memed eşkıya deli Durdu'ya katılır, ancak kıyıcığına katlanamadığı Deli Durdu'dan iki arkadaşıyla birlikte ayrılır. Memed, sıradan bir köy çocuğuyken, zulmedenler için eşkıyaya, köylüler içinse bir kurtarıcıya dönüşür.\r\n\r\n\"Bir yaşam biçimini bir halkın portresi olarak bölyesine veren bu romandan daha iyisi yazılamazdı. \"\r\n\r\nThe New York Times Book Review, (A.B.D)\r\n\r\n\"Şaşırtıcı, orijinal bir kitap.\"\r\n\r\nSunday, Times, (İngiltere)\r\n\r\n\"Epik boyutlara ulaşan ve muhteşem bir sona ulaşmak için hız kazanan öyküye kendinizi kaptırıyorsunuz.\"\r\n\r\nSunday Times, (İngiltere)\r\n\r\n\"Yaşar Kemal, şaşılacak ölçüde yaratıcı. \"\r\n\r\nThe Bookseller, (İngiltere)\r\n\r\n\"Yaşar Kemal, karakterlerini unutulmaz, seçkin ve gerçek hayattan daha da gerçekçi kılan detay zenginliği ile Rus edebiyatının kalitesine ulaşıyor.\"\r\n\r\nSunday Telegraph, (İngiltere)",
                               AuthorId=4, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9789750807145.jpg", UnitPrice=47, CategoryId = 3},
                    new Book(){Title="İnce Memed 2", Description="Otuz iki yıllık bir zaman diliminde yazılan İnce Memed dörtlüsü, düzene başkaldıran Memed'in ve insan ilişkileri, doğası ve renkleriyle Çukorava'nın öyküsüdür. Yaşar Kemal'in söyleşiyle \"içinde başlakdırma kurduyla doğmuş\" bir insanın, \"mecbur adam\"ın romanı. Abdi Ağa'nın zulmüyle köyünü terk etmek zorunda kalan Memed, Ağa'nın yeğeniyle evlendirilmek üzere olan Hatçe'yi kaçırır.\r\n\r\nAbdi Ağa'yı yaralayan, yeğenini de öldüren Memed eşkıya deli Durdu'ya katılır, ancak kıyıcığına katlanamadığı Deli Durdu'dan iki arkadaşıyla birlikte ayrılır. Memed, sıradan bir köy çocuğuyken, zulmedenler için eşkıyaya, köylüler içinse bir kurtarıcıya dönüşür.\r\n\r\n\"Bir yaşam biçimini bir halkın portresi olarak bölyesine veren bu romandan daha iyisi yazılamazdı. \"\r\n\r\nThe New York Times Book Review, (A.B.D)\r\n\r\n\"Şaşırtıcı, orijinal bir kitap.\"\r\n\r\nSunday, Times, (İngiltere)\r\n\r\n\"Epik boyutlara ulaşan ve muhteşem bir sona ulaşmak için hız kazanan öyküye kendinizi kaptırıyorsunuz.\"\r\n\r\nSunday Times, (İngiltere)\r\n\r\n\"Yaşar Kemal, şaşılacak ölçüde yaratıcı. \"\r\n\r\nThe Bookseller, (İngiltere)\r\n\r\n\"Yaşar Kemal, karakterlerini unutulmaz, seçkin ve gerçek hayattan daha da gerçekçi kılan detay zenginliği ile Rus edebiyatının kalitesine ulaşıyor.\"\r\n\r\nSunday Telegraph, (İngiltere)",
                               AuthorId=4, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9789750807145.jpg", UnitPrice=47, CategoryId = 3},
                    new Book(){Title="İnce Memed 3", Description="Otuz iki yıllık bir zaman diliminde yazılan İnce Memed dörtlüsü, düzene başkaldıran Memed'in ve insan ilişkileri, doğası ve renkleriyle Çukorava'nın öyküsüdür. Yaşar Kemal'in söyleşiyle \"içinde başlakdırma kurduyla doğmuş\" bir insanın, \"mecbur adam\"ın romanı. Abdi Ağa'nın zulmüyle köyünü terk etmek zorunda kalan Memed, Ağa'nın yeğeniyle evlendirilmek üzere olan Hatçe'yi kaçırır.\r\n\r\nAbdi Ağa'yı yaralayan, yeğenini de öldüren Memed eşkıya deli Durdu'ya katılır, ancak kıyıcığına katlanamadığı Deli Durdu'dan iki arkadaşıyla birlikte ayrılır. Memed, sıradan bir köy çocuğuyken, zulmedenler için eşkıyaya, köylüler içinse bir kurtarıcıya dönüşür.\r\n\r\n\"Bir yaşam biçimini bir halkın portresi olarak bölyesine veren bu romandan daha iyisi yazılamazdı. \"\r\n\r\nThe New York Times Book Review, (A.B.D)\r\n\r\n\"Şaşırtıcı, orijinal bir kitap.\"\r\n\r\nSunday, Times, (İngiltere)\r\n\r\n\"Epik boyutlara ulaşan ve muhteşem bir sona ulaşmak için hız kazanan öyküye kendinizi kaptırıyorsunuz.\"\r\n\r\nSunday Times, (İngiltere)\r\n\r\n\"Yaşar Kemal, şaşılacak ölçüde yaratıcı. \"\r\n\r\nThe Bookseller, (İngiltere)\r\n\r\n\"Yaşar Kemal, karakterlerini unutulmaz, seçkin ve gerçek hayattan daha da gerçekçi kılan detay zenginliği ile Rus edebiyatının kalitesine ulaşıyor.\"\r\n\r\nSunday Telegraph, (İngiltere)",
                               AuthorId=4, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9789750807145.jpg", UnitPrice=47, CategoryId = 3},
                    new Book(){Title="İnce Memed 4", Description="Otuz iki yıllık bir zaman diliminde yazılan İnce Memed dörtlüsü, düzene başkaldıran Memed'in ve insan ilişkileri, doğası ve renkleriyle Çukorava'nın öyküsüdür. Yaşar Kemal'in söyleşiyle \"içinde başlakdırma kurduyla doğmuş\" bir insanın, \"mecbur adam\"ın romanı. Abdi Ağa'nın zulmüyle köyünü terk etmek zorunda kalan Memed, Ağa'nın yeğeniyle evlendirilmek üzere olan Hatçe'yi kaçırır.\r\n\r\nAbdi Ağa'yı yaralayan, yeğenini de öldüren Memed eşkıya deli Durdu'ya katılır, ancak kıyıcığına katlanamadığı Deli Durdu'dan iki arkadaşıyla birlikte ayrılır. Memed, sıradan bir köy çocuğuyken, zulmedenler için eşkıyaya, köylüler içinse bir kurtarıcıya dönüşür.\r\n\r\n\"Bir yaşam biçimini bir halkın portresi olarak bölyesine veren bu romandan daha iyisi yazılamazdı. \"\r\n\r\nThe New York Times Book Review, (A.B.D)\r\n\r\n\"Şaşırtıcı, orijinal bir kitap.\"\r\n\r\nSunday, Times, (İngiltere)\r\n\r\n\"Epik boyutlara ulaşan ve muhteşem bir sona ulaşmak için hız kazanan öyküye kendinizi kaptırıyorsunuz.\"\r\n\r\nSunday Times, (İngiltere)\r\n\r\n\"Yaşar Kemal, şaşılacak ölçüde yaratıcı. \"\r\n\r\nThe Bookseller, (İngiltere)\r\n\r\n\"Yaşar Kemal, karakterlerini unutulmaz, seçkin ve gerçek hayattan daha da gerçekçi kılan detay zenginliği ile Rus edebiyatının kalitesine ulaşıyor.\"\r\n\r\nSunday Telegraph, (İngiltere)",
                               AuthorId=4, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9789750807145.jpg", UnitPrice=47, CategoryId = 3},
                    new Book(){Title="Dünyanın Merkezine Yolculuk", Description="Jeoloji profesörü Lidenbrock, Arne Saknussemm adında bir kâşifin dünyanın merkezine giden bir yolu tarif ettiği elyazmasına rastlar. Yeğeni Axel’in elyazmasındaki bilmeceyi deşifre etmesiyle profesörün ve Axel’in hayatı sonsuza dek değişir. Sessiz Hans'ın rehberliğinde, yeraltı dünyasındaki tehlikeli, şaşırtıcı ve ürkütücü yolculuklarında tarihöncesi canavarlarla ve türlü tuhaflıklarla karşılaşırlar. Bir Jules Verne klasiği olan Dünyanın Merkezine Yolculuk, harikalar diyarına yapılan hayalî bir yolculuğun tasviriyle okurlarını büyülemeye devam ediyor.",
                               AuthorId=2, ImageUrl="https://img1-kidega.mncdn.com/mnresize/200/307/UPLOAD/urunler/9786057475442.jpg", UnitPrice=24, CategoryId = 1},

                };
                context.Books.AddRange(books);
                context.SaveChanges();
            }
        }

        private static void seedAuthorIfNotExist(KidegaDbContext context)
        {
            if (!context.Authors.Any())
            {
                var authors = new List<Author>()
                {
                    new Author(){FirstName="Stefen", LastName="Zweig", Biography="Stefan Zweig 1881 yılında Viyana, Avusturya’da dünyaya gelmiştir. Oyun yazarı, romancı, gazeteci ve biyografi yazarıdır. Babası varlıklı bir sanayici olan Stefan küçük yaşlardan itibaren edebiyat, kültür alanında iyi bir eğitim görmüş Latince, Fransızca, İngilizce, Yunanca dillerini iyi şekilde konuşmaktadır. İlk şiirlerini lise yıllarında yazdı. Hugo Von ve Paul Verlaine şiirlerini Almanya ’caya çevirdi. 1.Dünya Savaşında gönüllü olarak Viyana’da savaş karargâhında Arşiv Memuru olarak çalıştı. Savaş bittikten sonra Avusturya’ya döndü Salzburg’a yerleşerek Frederike Von Winternit ile evlendi. \r\n\r\nBurada yaklaşık 20 yıl kaldı. Burada geçirdiği yıllar en verimli yıllarıydı yazarın. İnsanlarla güzel dostluklar kurdu ve bu dönemde edebiyatı doruklara kadar çıkardı. Salzburg’da geçen senelerinde Stefan Zweig Avrupa’nın düşünsel birliğinde ağırlığını koydu bir takım konferans ve makaleleri ile aşırılılığa karşı devamlı uyarılarda bulundu.\r\n\r\n1927yılında Duygu Karmaşası, Yıldızın Parladığı Anlar, Tarihsel Baş Minyatür isimli kitaplarını yayınladı. Stefan Zweig, Leo Tolstoy’un 100. Doğum yıldönümünde kutlamalara katılmak için Sovyetler Birliğine gitti.\r\n\r\n1933de Nazilerin yaktığı kitaplar içinde Yahudi kökenli Stefan Zweig’in de kitapları ’da yer alıyordu. Zweig bundan sonra ülkesini terk ederek Londra’ya yerleşmek zorunda kaldı. Burada da huzur bulamadı. \r\n\r\n1937 yılında karısı Frederike’den ayrıldı. Tam bir sene sonra Portekize giderken yanında Lotte Altman isimli bir kadın vardı. Bu sıralarda Avusturya, Alman Reich’ katılmıştı Stefan’da İngiliz vatandaşlığına geçmek için müracaat etmişti. Daha sonra Lotte Altman ile evlendi yazar. II. Dünya savaşında İngiliz tabiiyetine girerek New York, Arjantin, Portekiz’e gitti. \r\n\r\nBrezilyaya yerleşmeye karar veren yazar orada çok ünlü eseri Satranç Öyküsünü yayınladı.Oldukça üretken olması ile bilinen Zweig denemeler yaptı birçok konuda. Lirik şiirler yazdı, Trajedi türünde sahne eserleri, biyografi alanlarında denemeler yaptı.\r\n\r\nBiyografi alanındaki çalışmalarını Freud’a ve psikolojiye olan ilgisinden dolayı etkilenmişti. Balzac, Dickens, Dostoyevski gibi üç büyüğün biyografilerini hazırladı.\r\n\r\nPatlak veren savaş tüm dünyayı yakmaktaydı buda en çok Zweig’i üzüyordu. Büyük yazar Zweig Alman bir şair olan Kleist kendini ve eşini silahla vurarak intihar etmesinden etkilenmiş ve karısı Lotte ile bu benzerliği yaşamak ister.\r\n\r\nZweig bunun sonucunda bir soda şişesine bol miktarda Veronal denilen zehri ilave eder ve bu şişeden üç yudum alarak eşine şişeyi uzatır şunları söyler’ Yanıma gelmek arzusundaysan eğer bunu istediğin zaman yapabilirsin der’\r\n\r\nBunun üzerine Lotte ona son olarak ‘ beni seviyor musun’ diye sorar Zweig evet cevabını verir. Lotte şişenin tamamını içer ve eşinin yanına uzanır. Son uykularına yatarlar.\r\n\r\nHitlere karşı nefret duyan Zweig çiftinin intiharına neden olmuş fakat tarih tekerrür ederek 3 yıl sonra Nazilerin Rusya’ya karşı yenilmesinden sonra Hitler eşi ile birlikte intihar etmiştir. "},
                    new Author(){FirstName="Jules", LastName="Verne", Biography="Jules Gabriel Verne 1828 yılında Fransa’nın Nantes kasabasında doğmuştur. Ailesinin çiftliğinde çocukluğunu geçirmiş olan Verne’nin babası avukat annesi de çok katı kurallı bir İskoç'tu."},
                    new Author(){FirstName="Edgar Rice", LastName="Burroughs", Biography="Edgar Rice Burroughs, Bilmece ve Bulmacalar, Çocuk & Gençlik, Çocuk Öykü & Roman kategorilerinde eserler yazmış bir yazardır. Başlıca kitapları alfabetik sırayla; A Princess of Mars, At the Earth's Core, Tarzan olarak sayılabilir. Edgar Rice Burroughs kitapları; Evans Yayınları, Gece Kitaplığı aracılığıyla kitapseverlerle buluşmuştur. Edgar Rice Burroughs tarafından yazılan son kitap 'A Princess of Mars', Gece Kitaplığı tarafından okurların beğenisine sunulmuştur."},
                    new Author(){FirstName="Yaşar", LastName="Kemal", Biography="Cumhuriyet döneminin en önemli yazarlarından olan Yaşar Kemal 6 Ekim 1923 yılında Osmaniye’de doğmuştur. Babasının adı Sadık Efendi’dir. Çiftçilikle uğraşmaktaydı. Annesinin adı ise Nigar Hanım’dır. Roman, öykü ve senaryo türünde eserler vermiştir.\r\n\r\nİlk romanı olan İnce Memed yaklaşık 40 dile çevrilmiştir ve kitapları yurtdışında 140’dan fazla baskı yapmıştır. Nobel Edebiyat Ödülüne ilk aday olarak gösterilen yazardır. Yaşar Kemal 28 Şubat 2015 yılında organ yetmezliği nedeniyle kaldırıldığı hastanede yaşamını yitirmiştir. Naaşı Zincirlikuyu Mezarlığına gömülmüştür.\r\n\r\nYaşar Kemal yazdığı roman, hikaye ve röportajlarıyla unutulmaz isimler arasına girmeyi başarmıştır. Uzun yılar birlikte yaşadığı köy insanının hayatlarını onların içinden biri olarak yazmıştır. \r\n\r\nÖzellikle Çukurova insanının yaşamı, Toros dağlarında yaşayan köylülerin yaşamı, sıkıntıları ırgatları ve ırgatlarla köy ağalarının ilişkilerini, eşkıyaları ve kan davalarını eserlerinde işlemiştir.\r\n\r\nŞiirsel bir üslubu olan Yaşar Kemal’in dili canlı ve temizdir. Ağıtlara, türkülere, tekerlemelere, atasözlerine ve halk söyleyişlerine eserlerinde yer vermiştir. Bu da eserlerinin zengin olmasını sağlamıştır.\r\n\r\nÜçlü dizeler halinde (nehir roman) romanlarını yazmıştır. Anadolu insanının bütün zenginliklerinden, masalarından, efsanelerinden ve destanlarından yaralanan Yaşar Kemal roman ve hikayelerini röportaj havasıyla yazmıştır.\r\n\r\nBaşarılı doğa betimleri yapmış ve yazdığı eserlerinde özgünlüğü yakalamıştır. Ayrıca geleneksel ve çağdaşı, hayal ile gerçeği bir arada vermiştir."},

                };
                context.Authors.AddRange(authors);
                context.SaveChanges();
            }
        }
    }
}
